flight_id) references Flight (flight_id),foreign key (customer_id) references Customer (customer_id),primary key (ticket_id))";mysqli_query($con,$query);$query = "create table Airport(airport_name varchar (20),availability varchar (20),weather varchar (20),coordinate varchar(20),city varchar(20),primary key(airport_name),foreign key (city) references Country(city))";mysqli_query($con,$query);$query = "create table UserType(typename varchar(20),feedbackOp varchar (20),minPurchase varchar(20),primary key(typename, feedbackOp))";mysqli_query($con,$query);$query = "create table isKindOfUser(customer_id varchar (20),type varchar (15),period varchar(20),primary key(customer_id)foreign key (customer_id) references Customer,foreign key (type) references Usertype)";mysqli_query($con,$query);$query = "create table FliesWith(customer_id varchar (20),airway_id varchar (20),customer_airway varchar(20),primary key (customer_id,airway_id),foreign key (airway_id) references Airway (airway_id),foreign key (customer_id) references Customer (customer_id))";mysqli_query($con,$query);$query = "create table FliesTo(flight_id varchar (20),airport_name varchar(20),departure_date DATETIME ,departure_airport varchar (20),primary key(flight_id,airport_name),foreign key (flight_id) references Flight (flight_id),foreign key(departure_airport) references Airport (airport_name))";mysqli_query($con,$query);$query = "create table FliesFrom(flight_id varchar (20),arrival_date DATETIME ,arrival_airport varchar (20),primary key (flight_id),foreign key (flight_id) references Flight (flight_id),foreign key(arrival_airport) references Airport (airport_name))";mysqli_query($con,$query);$query = "create table Distance(arrival_port varchar(20),departure_port varchar(20),length varchar(15),primary key (arrival_port,departure_port),foreign key (arrival_port) references Airport (arrival_port),foreign key (departure_port) references Airport (depature_port))";mysqli_query($con,$query);$query = "create table AddressOf(address_id varchar (20),householder varchar (20),primary key(address_id),foreign key (address_id) references Address (address_id))";mysqli_query($con,$query);$query = "create table UsesDiscount(customer_id varchar (20),discount_type varchar(20),discount_amount int,primary key (customer_id,discoun_type),foreign key (customer_id) references Customer (customer_id),foreign key (discount_type) references Discount (discount_type))";mysqli_query($con,$query);$query = "create table HiresPlane(airway_id varchar (20),airplane_id varchar (20),period varchar (15),primary key(period,airway_id,airplane_id),foreign key (airway_id) references Airway (airway_id),foreign key (airplane_id)references Airplane (airplane_id)";mysqli_query($con,$query);}function AirwayPopulator($con){$sql= "INSERT INTO Airway (airway_id, airway_pass, user_name,mailAddress,phoneNumber)VALUES ('50000', 'ana2018', 'Anadolujet', 'ana@gmail.com', '02124216380')";mysqli_query($con, $sql);$sql= "INSERT INTO Airway (airway_id, airway_pass, user_name,mailAddress,phoneNumber)VALUES ('50001', 'atl2018', 'AtlasGlobal', 'atl@gmail.com', '02124216381')";mysqli_query($con, $sql);$sql= "INSERT INTO Airway (airway_id, airway_pass, user_name,mailAddress,phoneNumber)VALUES ('50002', 'bor2018', 'Borajet', 'bor@gmail.com', '02124216386')";mysqli_query($con, $sql);$sql= "INSERT INTO Airway (airway_id, airway_pass, user_name,mailAddress,phoneNumber)VALUES ('50003', 'onu2018', 'Onur Air', 'onu@gmail.com', '02124216387')";mysqli_query($con, $sql);$sql= "INSERT INTO Airway (airway_id, airway_pass, user_name,mailAddress,phoneNumber)VALUES ('50004', 'peg2018', 'Pegasus Airlines', 'peg@gmail.com', '02124216384')";mysqli_query($con, $sql);$sql= "INSERT INTO Airway (airway_id, airway_pass, us